module.exports = function (source) {
    // 通过this.callback告诉webpack返回的结果
    thihs.callback(null, source, sourceMap);
    // 当使用this.callback返回内容时，该loader必须返回undefined，
    // 以让webpack知道Loader返回的结果在this.callback中，而不是return中
